CREATE TABLE MB(
   Code                 VARCHAR(4) NOT NULL PRIMARY KEY
  ,Motherboard_name     VARCHAR(21) NOT NULL
  ,Motherboard_type     VARCHAR(5) NOT NULL
  ,Socket               VARCHAR(7) NOT NULL
  ,Number_of_DDR4_Slots INTEGER  NOT NULL
  ,Number_of_DDR5_Slots INTEGER  NOT NULL
  ,Number_of_M2_Slots   INTEGER  NOT NULL
  ,Form_factor          VARCHAR(4) NOT NULL
  ,Price                INTEGER  NOT NULL
  ,Stock                INTEGER  NOT NULL
  ,Description          VARCHAR(30)
);
